from TwitterAPI import TwitterAPIapi = TwitterAPI(consumer_key, consumer_secret, access_token_key, access_token_secret)def searchtweetsbykeyword(keyword):	r = api.request('search/tweets', {'q':keyword,'lang': 'en'})	results = []	for item in r.get_iterator():		results.append(item.get("text"))	return results[:10]def searchbylocation(keyword):	r = api.request('statuses/filter', {'locations':keyword})	results = []	for item in r.get_iterator():		results.append(item.get("text"))	return results#while(1):#	str = raw_input()#	if(str=="quit"):#		break#	else:#		res = searchtweetsbykeyword(str)#		print res